from openpyxl import load_workbookimport pandas as pdfrom Categoriser import SimpleCategoriserfrom Item import Itempath = r"data/Duct Fitting Schedule Low Pressure Insulated Cladded Rect.xlsx"wb = load_workbook(path)sheet = pd.DataFrame(data=wb.active.values)sheet.columns = sheet.iloc[0]sheet = sheet[sheet["Insulation Type"].astype(str) != "None"]sheet = sheet[["Size", "Count", "Area", "Surface Area"]].iloc[1:, :]boq = [    Item(row["Size"], row["Count"], row["Area"], row["Surface Area"], SimpleCategoriser())    for _, row in sheet.iterrows()]summarised_boq = {    category: sum([item.count * item.boq_area for item in boq if item.category == category])    for category in range(1, 6)}print(summarised_boq)